pragma solidity ^0.8.9;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

abstract contract ISwapable {
    string public pairName;

    function getAskPrice(uint256 _ERC20TokenAmount) public virtual returns (uint256);

    function swapBaseForQuoteToken(uint256 _ERC20TokenAmount) external payable virtual;

    function getBidPrice(uint256 _satiAmount) public virtual returns (uint256);

    function swapQuoteForBaseToken(uint256 _satiAmount) external payable virtual;
}
","keccak256":"0x72c18eb627599a82d8752582d858b52f2e65e1f16605a981b6fb984da9b1653d","license":"MIT"},"contracts/Ratable.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract Ratable {
    event Rate(int256 indexed scaledPrice, uint256 indexed timeStamp);

    AggregatorV3Interface internal priceFeed;

    constructor(address _priceFeedContract) {
        priceFeed = AggregatorV3Interface(_priceFeedContract);
    }

    function scaleTokenForMultiplication(uint256 _tokenAmount, uint8 _decimals)
        internal
        pure
        returns (uint256)
    {
        return _tokenAmount / uint256(10**uint256(_decimals));
    }

    function scaleTokenForDivision(uint256 _tokenAmount, uint8 _decimals)
        internal
        pure
        returns (uint256)
    {
        return _tokenAmount * uint256(10**uint256(_decimals));
    }

    function scalePrice(
        int256 _price,
        uint8 _priceDecimals,
        uint8 _decimals
    ) internal pure returns (int256) {
        if (_priceDecimals < _decimals) {
            return _price * int256(10**uint256(_decimals - _priceDecimals));
        } else if (_priceDecimals > _decimals) {
            return _price / int256(10**uint256(_priceDecimals - _decimals));
        }
        return _price;
    }

    function getScaledRate(uint8 _scalingDecimal) public returns (int256) {
        (, int256 price, , uint256 timeStamp, ) = priceFeed.latestRoundData();
        uint8 decimals = priceFeed.decimals();

        int256 scaledPrice = scalePrice(price, decimals, _scalingDecimal);

        emit Rate(scaledPrice, timeStamp);

        return scaledPrice;
    }
}
","keccak256":"0x686447223ce97bcc66c4763c3055d461dd4af849d680e4042bc257bb17029734","license":"MIT"},"contracts/SwapContractFactory.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "./AstroToken.sol";
import "./Utils.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./ERC20TokensSwap.sol";

contract SwapContractFactory is Ownable, Utils {
  struct SwapAndTokenAddresses {
    address swapContractAddress;
    address quoteTokenAddress;
    address baseTokenAddress;
    bool deployed;
  }

  event DeployedSwapContract(
    address indexed swapContractAddress,
    address indexed baseTokenAddress,
    address indexed quoteTokenAddress
  );

  string[] public swapPairs;
  mapping(string => SwapAndTokenAddresses) public deployedSwapContractsRegistry;

  function deployNewSwapContract(
    ERC20 _baseToken,
    ERC20 _quoteToken,
    address _exchangeRate
  ) external onlyOwner {
    ERC20TokensSwap swapContract = new ERC20TokensSwap(_baseToken, _quoteToken, _exchangeRate);

    string memory deployedPair = swapContract.pairName();

    require(
      !deployedSwapContractsRegistry[deployedPair].deployed,
      "already deployed"
    );

    swapPairs.push(deployedPair);

    deployedSwapContractsRegistry[deployedPair] = SwapAndTokenAddresses({
      swapContractAddress: address(swapContract),
      quoteTokenAddress: address(_quoteToken),
      baseTokenAddress: address(_baseToken),
      deployed: true
    });

    emit DeployedSwapContract(address(swapContract), address(_baseToken), address(_quoteToken));
  }

  function getAllSwapPairs() external view returns (string[] memory) {
    return swapPairs;
  }
}
","keccak256":"0xf2099f3688c093c1c4bb6db21db13105ed68d799f4b5df32cce18ea89bb22e87","license":"MIT"},"contracts/Swapable.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./Ratable.sol";
import "./Utils.sol";
import "./ISwapable.sol";

abstract contract Swapable is Ratable, Utils, ISwapable {
    uint8 private precisionDecimals = 18;

    event SwapTransferInfo(
        address indexed beneficiary,
        uint256 indexed amountSent,
        uint256 indexed amountReceived
    );

    event SwapRateInfo(
        string indexed exchangeType,
        uint256 indexed sellingAmount,
        uint256 indexed buyingAmount
    );

    function requireHasEnoughToken(
        ERC20 _tokenAddress,
        address _addressToValidate,
        uint256 _requiredAmount
    ) internal view {
        require(
            _tokenAddress.balanceOf(_addressToValidate) >= _requiredAmount,
            concatenateTwo("Not enough ", _tokenAddress.symbol())
        );
    }

    function getAvailableTokenAmount(ERC20 _tokenToCheckBalanceOf) internal view returns (uint256) {
        return _tokenToCheckBalanceOf.balanceOf(address(this));
    }

    function getQuoteTokenAmountForFromRate(uint256 _ERC20TokenAmount) internal returns (uint256) {
        int256 exchangeRate = getScaledRate(precisionDecimals);

        return
            scaleTokenForMultiplication(_ERC20TokenAmount, precisionDecimals) *
            uint256(exchangeRate);
    }

    function getBaseTokenAmountFromRate(uint256 _satiAmount) internal returns (uint256) {
        int256 exchangeRate = getScaledRate(precisionDecimals);

        return scaleTokenForDivision(_satiAmount, precisionDecimals) / uint256(exchangeRate);
    }
}
","keccak256":"0xb00750936c2b5f00b92a8bb148ffb78ae64458edf6b584a2551b1469911ca59d","license":"MIT"},"contracts/Utils.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

contract Utils {
    function concatenateTwo(string memory partOne, string memory partTwo)
        internal
        pure
        returns (string memory)
    {
        return string(abi.encodePacked(partOne, partTwo));
    }

    function concatenateThree(
        string memory partOne,
        string memory partTwo,
        string memory partThree
    ) internal pure returns (string memory) {
        return concatenateTwo(concatenateTwo(partOne, partTwo), partThree);
    }
}
